import { 
    ALL_PRODUCT_LOADING,
    ALL_PRODUCT_SUCCESS,
    ALL_PRODUCT_FAIL,
    PRODUCT_DETAILS_LOADING,
    PRODUCT_DETAILS_SUCCESS,
    PRODUCT_DETAILS_FAIL,
    CLEAR_ERROR
} 
from "./actionType"

const initialState = { 
    products: [], 
    loading: true,
    productsCount: 0
};

export const productReducer = ( state = initialState, action) =>{
     
    switch(action.type){
        case ALL_PRODUCT_LOADING:
        return{
                loading : true,
                products : [],
                productsCount: 0
        }
        case ALL_PRODUCT_SUCCESS : 
        return{  
              loading : false,
              products : action.payload,
           
        }
        case ALL_PRODUCT_FAIL : 
        return{
             loading : false,
             error : action.payload,
        }
        case CLEAR_ERROR : 
        return{
             ...state,
             error : null,
        }

        default: 
         return{
            state
         }

    }
};


export const productDetailsReducer = ( state = {products:{} }, {type,payload}) =>{
     
    switch(type){
        case PRODUCT_DETAILS_LOADING:
        return{
                loading : true,
                ...state,
        }
        case PRODUCT_DETAILS_SUCCESS : 
        return{
             
              loading : false,
              products : payload,
        }
        case PRODUCT_DETAILS_FAIL : 
        return{
             loading : false,
             error : payload,
        }
        case CLEAR_ERROR : 
        return{
             ...state,
             error : null,
        }

        default: 
         return{
            state
         }

    }
}
