import { 
    ALL_PRODUCT_REQUEST,
    ALL_PRODUCT_SUCCESS,
    ALL_PRODUCT_FAIL,
    CLEAR_ERROR
} 
from "../ActionType/productActionType"


export const productReducer = ( state = {products: []}, action) =>{
     
    switch(action.type){
        case ALL_PRODUCT_REQUEST:
        return{
                loading : true,
                product : []
        }
        case ALL_PRODUCT_SUCCESS : 
        return{
              loading : false,
              product : action.payload.products,
              productsCount : action.payload.productsCounts 
        }
        case ALL_PRODUCT_FAIL : 
        return{
             loading : false,
             error : action.payload,
        }
        case CLEAR_ERROR : 
        return{
             ...state,
             error : null,
        }

        default: 
         return{
            state
         }

    }
}
