import './App.css';
import { Routes, Route } from "react-router-dom";
import { SendOTP } from './components/Email/Form';
import { Login } from './components/Login';
import { Register } from "./components/Register";
import Products from "./components/Products/Products";
// import axios from 'axios';

import { useEffect } from 'react';
import { useDispatch ,useSelector } from 'react-redux';

import {getProducts} from './Features/Products/action'

function App() {
   const dispatch = useDispatch();

   async function getUser() {

    await fetch('http://localhost:4500/products/')
     .then((response) => response.json())
     .then((data) =>dispatch(getProducts(data)))
  }
  getUser()

  const { token } = useSelector((state) => ({
    token: state.productsState.products,
   }), function (prev, cur) {
    if (prev.token === cur.token) {
        return true;
    }
    else {
        return false;
    }

});


  return (
    <div className="App">
      <Routes>
        <Route path="login" element={<Login />}></Route>
        <Route path="register" element={<Register />}></Route>
        <Route path="/verification" element={<SendOTP />}></Route>
        <Route path ="/products" element ={<Products/>}> </Route>
      </Routes>
    </div>
  );
}

export default App;
